        private void UpdateOffset(int bank, int sample, String text)
        {
            Banks[bank].samples[sample].offset = int.Parse(text, NumberStyles.HexNumber);
            switch (bank)
            {
                case 0:
                    setCtrl0();
                    break;
                case 2:
                    setCtrl2();
                    break;
                case 3:
                    setCtrl3();
                    break;
                case 4:
                    setCtrl4();
                    break;
                case 5:
                    setCtrl5();
                    break;
                case 6:
                    setCtrl6();
                    break;
                case 7:
                    setCtrl7();
                    break;
                    //case 8:
                    //    setCtrlCom();
                    //    break;
            }

        }

