        private void UpdateDepend(int bank, int sample, String text)
        {
            if (text.Contains("0x"))
            {
                Banks[bank].samples[sample].depends[0] = int.Parse(text, NumberStyles.HexNumber);
            }
            else
            {
                Banks[bank].samples[sample].depends[0] = int.Parse(text);
            }
            switch (bank)
            {
                case 0:
                    setCtrl0();
                    break;
                case 2:
                    setCtrl2();
                    break;
                case 3:
                    setCtrl3();
                    break;
                case 4:
                    setCtrl4();
                    break;
                case 5:
                    setCtrl5();
                    break;
                case 6:
                    setCtrl6();
                    break;
                case 7:
                    setCtrl7();
                    break;
                    //case 8:
                    //    setCtrlCom();
                    //    break;
            }

        }
